echo -e "\nðŸ”§ Info System\n---------------------"; os=$(lsb_release -d | cut -f2); kernel=$(uname -r); hostname=$(hostname); ip_private=$(hostname -I | awk '{print $1}'); ip_public=$(curl -s https://api.ipify.org); cpu_model=$(lscpu | grep 'Model name' | awk -F ':' '{print $2}' | sed 's/^ *//'); cores=$(nproc); total_ram=$(free -h | awk '/Mem:/ {print $2}'); used_ram=$(free -h | awk '/Mem:/ {print $3}'); echo "Hostname: $hostname"; echo "Private IP: $ip_private"; echo "Public IP: $ip_public"; echo "OS: $os"; echo "Kernel: $kernel"; echo "CPU: $cpu_model ($cores cores)"; echo "Total RAM: $total_ram"; echo -e "\nðŸ“Š Info Usage\n---------------------"; disk_usage=$(df -h / | awk 'NR==2 {print $3 " of " $2 " (" $5 " used)"}'); mem_percent=$(free | awk '/Mem:/ {printf "%.0f", $3/$2 * 100}'); swap=$(free | awk '/Swap:/ { if ($2 == 0) { print 0 } else { printf "%.0f", $3/$2 * 100 } }'); cpu=$(top -bn1 | grep "Cpu(s)" | awk '{print 100 - $8"%"}'); processes=$(ps aux | wc -l); uptime=$(uptime -p); time=$(date); # Storage usage
disk_total=$(df -k / | awk 'NR==2 {print $2 / 1024 / 1024 " GB"}')  # Total storage
disk_used=$(df -k / | awk 'NR==2 {print $3 / 1024 / 1024 " GB"}')   # Used storage
disk_percent=$(df -h / | awk 'NR==2 {print $5}')  # Used percentage
echo "Storage usage: $disk_percent (used $disk_used of $disk_total)"; # Memory usage
echo "Memory usage: ${mem_percent}% (used $used_ram of $total_ram)"; echo "Swap usage: ${swap}%"; echo "CPU Usage: ${cpu}"; echo "Processes: $processes"; echo "Uptime: $uptime"; echo "Time: $time"
